var tripcode = require('../');
var benchmark = require('benchmark');

var suite = new benchmark.Suite;

var SALT_TABLE = '.............................................../0123456789A' +
  'BCDEFGABCDEFGHIJKLMNOPQRSTUVWXYZabcdefabcdefghijklmnopqrstuvwxyz..........' +
  '..........................................................................' +
  '.................................................';

var SALT_ARRAY = SALT_TABLE.split('');

suite.add('array', function() {
  SALT_ARRAY[11];
});

suite.add('string', function() {
  SALT_TABLE.charAt(11);
});

suite.on('cycle', function(event) {
  console.log(String(event.target));
});

suite.on('complete', function() {
  console.log('Fastest is ' + this.filter('fastest').pluck('name'));
});

suite.run({'async': true})